import { useState } from "react";
import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import axios from "axios";
import { useLocation } from "react-router";

const getParmaId = () =>{ 
  const paramsId = useLocation();
  const productId = paramsId.pathname.slice(9, 36);
  console.log("parmas", productId)
  return productId;

}
export const fetchSingleProducts = createAsyncThunk(
  "singleProducts/fetchSingleProducts",
  async () => {
   
    const { data } = await axios.get(`products/${getParmaId}`)
    return data
  }
);

const singleProductSlice = createSlice({
  name: "singleProducts",
  initialState: { isLoading: true, errMess: null, productsArray: [] },
  reducers: {},
  extraReducers: {
    [fetchSingleProducts.pending]: (state) => {
      state.isLoading = true;
    },
    [fetchSingleProducts.fulfilled]: (state, action) => {
      state.isLoading = false;
      state.errMess = null;
      state.productsArray = action.payload;
    },
    [fetchSingleProducts.rejected]: (state, action) => {
      state.isLoading = false;
      state.errMess = action.error ? action.error.message : "Fetch failed";
    },
  },
});

export const singleProductsReducer = singleProductSlice.reducer;
