import Header from '../../components/header/Header'
import Footer from "../../components/footer/Footer";
import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import axios from "axios";
import {
  addToCart,
  clearCart,
  decreaseCart,
  getTotals,
  removeFromCart,
} from "../../redux/features/CartSlice";

import { Link , useNavigate} from "react-router-dom";

const CartScreen = () => {
    const cart = useSelector((state) => state.getCart);
    // const user = useSelector((state) => state.auth);
    const dispatch = useDispatch();
    const {cartItems} = cart
  
    useEffect(() => {
      dispatch(getTotals());
    }, [cart, dispatch]);
  
    const handleAddToCart = (course) => {
      dispatch(addToCart(course));
    };
    const handleDecreaseCart = (course) => {
      dispatch(decreaseCart(course));
    };
    const handleRemoveFromCart = (course) => {
      dispatch(removeFromCart(course));
    };
    const handleClearCart = () => {
      dispatch(clearCart());
    };
  
    const handleCheckout = () => {
      axios
        .post(`/stripe`, {
          cartItems,
          // userId: user._id,
        })
        .then((response) => {
          if (response.data.url) {
            window.location.href = response.data.url;
          }
        })
        .catch((err) => console.log(err.message));
    };
    return (
        <>
            <Header />

            <div class="container overflow-hidden text-center mt-5 pt-4 mb-4">
                <div class="py-3 rounded mb-3" style={{
                     background: "#fae6ae"
                }}>
                    A simple warning alert—check it out!
                </div>
                <div class="row gx-3">
                <h2>Shopping Cart</h2>
        {cart.cartItems.length === 0 ? (
          <div className="cart-empty">
            <p>Your cart is currently empty</p>
            <div className="start-shopping">
              <Link to="/newcourse">
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  width="20"
                  height="20"
                  fill="currentColor"
                  className="bi bi-arrow-left"
                  viewBox="0 0 16 16"
                >
                  <path
                    fillRule="evenodd"
                    d="M15 8a.5.5 0 0 0-.5-.5H2.707l3.147-3.146a.5.5 0 1 0-.708-.708l-4 4a.5.5 0 0 0 0 .708l4 4a.5.5 0 0 0 .708-.708L2.707 8.5H14.5A.5.5 0 0 0 15 8z"
                  />
                </svg>
                <span>Start Shopping</span>
              </Link>
            </div>
          </div>
        ) : (
          <div>
            <div className="titles">
              <h3 className="product-title">Product</h3>
              <h3 className="price">Price</h3>
              <h3 className="quantity">Quantity</h3>
              <h3 className="total">Total</h3>
            </div>
            <div className="cart-items">
              {cart.cartItems &&
                cart.cartItems.map((cartItem) => (
                  <div className="cart-item" key={cartItem.id}>
                    <div className="cart-product">
                      <img src={cartItem.image} alt={cartItem.name} />
                      <div>
                        <h3>{cartItem.name}</h3>
                        <p>{cartItem.description}</p>
                        <button onClick={() => handleRemoveFromCart(cartItem)}>
                          Remove
                        </button>
                      </div>
                    </div>
                    <div className="cart-product-price">${cartItem.cost}</div>
                    <div className="cart-product-quantity">
                      <button onClick={() => handleDecreaseCart(cartItem)}>
                        -
                      </button>
                      <div className="count">{cartItem.cartQuantity}</div>
                      <button onClick={() => handleAddToCart(cartItem)}>+</button>
                    </div>
                    <div className="cart-product-total-price">
                      ${cartItem.cost * cartItem.cartQuantity}
                    </div>
                  </div>
                ))}
            </div>
            <div className="cart-summary">
              <button className="clear-btn" onClick={() => handleClearCart()}>
                Clear Cart
              </button>
              <div className="cart-checkout">
                <div className="subtotal">
                  <span>Subtotal</span>
                  <span className="amount">${cart.cartTotalAmount}</span>
                </div>
                <p>Taxes and shipping calculated at checkout</p>
                <button >Check out</button>
                <div className="continue-shopping">
                  <Link to="/newcourse">
                    <svg
                      xmlns="http://www.w3.org/2000/svg"
                      width="20"
                      height="20"
                      fill="currentColor"
                      className="bi bi-arrow-left"
                      viewBox="0 0 16 16"
                    >
                      <path
                        fillRule="evenodd"
                        d="M15 8a.5.5 0 0 0-.5-.5H2.707l3.147-3.146a.5.5 0 1 0-.708-.708l-4 4a.5.5 0 0 0 0 .708l4 4a.5.5 0 0 0 .708-.708L2.707 8.5H14.5A.5.5 0 0 0 15 8z"
                      />
                    </svg>
                    <span>Continue Shopping</span>
                  </Link>
                </div>
              </div>
            </div>
          </div>
        )}
                    {/* <div class="col-12 col-md-8">
                        <div class="d-block d-md-flex px-4">
                            <div className="mb-2 p-4 w-100">
                                <img src='https://images.unsplash.com/photo-1580480055273-228ff5388ef8?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxzZWFyY2h8MjN8fGZ1cm5pdHVyZXxlbnwwfHwwfHw%3D&auto=format&fit=crop&w=1000&q=60' alt='' className='' style={{ height: '320px' }} />
                            </div>

                            <div className='mb-2 p-4 w-100'>
                                <div className='blogName w-100 d-flex align-items-center justify-content-between'><h3>Name occaecat amet pariatur dolore dolore et non mollit minim. </h3></div>
                                <p className='w-100'>Discription ullamco tempor culpa ad dolore  reprehenderit.</p>
                                <p>Sit cillum est aliquip dolor ullamco nulla enim id laborislaborum est. Proident culpa veniam laboris fugiat labore.</p>
                                <Link to="/" className='text-decoration-none'>Read more</Link>
                            </div>
                        </div>
                    </div>
                    <div class="col-12 col-md-4">
                        <div class="p-3 border bg-light">Pay Here</div>
                    </div> */}
                </div>
            </div>

            <Footer />
        </>

    )
}

export default CartScreen
